INSERT INTO `videos` (`id`, `name`, `slug`, `description`, `embed`, `created_at`, `updated_at`) VALUES
(1,	'Scala dei Sapori 2014 (video)',	'scala-dei-sapori-2014-video',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Tre minuti presi dall\'edizione del 2014, con riprese aeree e sul terreno, per farsi un\'idea sulla Scala Dei Sapori.</span><br></p>',	'https://www.youtube.com/embed/yVxyAnVX4ic',	'2014-10-01 05:07:34',	'2017-05-01 05:13:14'),
(2,	'Edizione 2013',	'edizione-2013',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Edizione del 2013, con qualche intervista alle associazioni e ai rappresentanti degli stands. 30minuti.</span><br></p>',	'https://www.youtube.com/embed/CJCZr9oEDrY',	'2013-10-01 02:10:12',	'2017-05-01 03:12:51'),
(3,	'Ninna Nanna - Inaugurazione Piazza',	'ninna-nanna-inaugurazione-piazza',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Serata inaugurale della scala dei sapori 2015 e della nuova Piazza di Primolano. Un video della ninna nanna cantata da P.P.</span><br></p>',	'https://www.youtube.com/embed/FzGRsQnurVg',	'2015-10-01 05:15:10',	'2017-05-01 05:15:10'),
(4,	'FelicePantone',	'felicepantone',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Un simpatico musico cantastorie alla manifestazione della scala dei sapori.</span><br></p>',	'https://www.youtube.com/embed/98_BT_geOWo',	'2015-10-01 05:16:02',	'2017-05-01 05:16:02'),
(5,	'Marionette Galera',	'marionette-galera',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">El prete, la finanza el tabacco</span><br></p>',	'https://www.youtube.com/embed/T5FpS1-VE5g',	'2015-10-01 05:16:56',	'2017-05-01 05:16:56'),
(6,	'Il Tabacco di Luciano',	'il-tabacco-di-luciano',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Il Tabacco di Luciano - CONTRA CA\' CORNARO PALIO DI ROMANO</span><br style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\"><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">In piazza Libertà a Primolano, in occasione della manifestazione la \"Scala dei Sapori\", Luciano ha presentato la suo piccola produzione di tabacco, con storie e aneddoti dei tempi passati.</span><br></p>',	'https://www.youtube.com/embed/e6d-FfPwDwU',	'2015-10-01 05:45:51',	'2017-05-01 05:45:51'),
(7,	'Artisti di strada',	'artisti-di-strada',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Un simpatico mimo in piazza libertà, alla scala dei sapori edizione 2015</span><br style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\"><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Primolano 13 settembre 2015</span><br></p>',	'https://www.youtube.com/embed/i-HPw_3oygQ',	'2015-10-01 05:46:37',	'2017-05-01 05:46:37'),
(8,	'Scala dei Sapori 2015',	'scala-dei-sapori-2015',	'<p><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Ecco il video ufficiale della Scala dei Sapori 2015. Ci scusiamo per il ritardo, ma MEGLIO TARDI CHE MAI!!!</span><br style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\"><span style=\"color: rgb(95, 95, 95); font-family: &quot;Open Sans&quot;, arial, sans-serif; font-size: 16px;\">Un grazie a tutti e buona visione</span><br></p>',	'https://www.youtube.com/embed/gHOB-AWbIQU',	'2015-10-01 05:47:31',	'2017-05-01 05:47:31');
